
SELECT u.last_name, u.first_name, MONTH(c.date_cours) AS mois, CASE MONTH(c.date_cours) WHEN 1 THEN 'Jan' WHEN 2 THEN 'Fév' WHEN 3 THEN 'Mar' WHEN 4 THEN 'Avr' WHEN 5 THEN 'Mai' WHEN 6 THEN 'Juin' WHEN 7 THEN 'Juil' WHEN 8 THEN 'Août' WHEN 9 THEN 'Sep' WHEN 10 THEN 'Oct' WHEN 11 THEN 'Nov' WHEN 12 THEN 'Déc' END AS nom_mois, c.type_cours, COUNT(i.id) AS total_presences FROM inscriptions i JOIN cours c ON i.cours_id = c.id JOIN utilisateurs u ON i.utilisateur_id = u.id WHERE i.status_id = 1 -- Présences validées AND i.utilisateur_id = 151 -- Filtrer par utilisateur spécifique GROUP BY u.last_name, u.first_name, mois, c.type_cours ORDER BY u.last_name, u.first_name, mois, c.type_cours;

